mapper.select:

	  select 포인트 as point
      from user
      where 카카오번호 = #{kkono}

mapper.update:

update user
set 포인트 = (포인트 - ${price})
where 카카오번호 = #{kkono}


update user
set 포인트 = (포인트 + ${price})
where 카카오번호 = #{kkono2}


update shop
set 가격 = #{price}
where 템번호 = #{itemno}


update shop
set 삭제 = 'Y'
where 템번호 = #{itemno}
